package ru.job4j.exam.collections.generics.iobook;import java.io.FileInputStream;import java.io.FileOutputStream;import java.io.IOException;import java.io.InputStreamReader;/** * этот код открывает файл для чтения, открывает файл для записи и * поочередно считывает байты из входного файла, записывая их в * выходной файл, пока не достигнет конца файла. Затем он закрывает оба файла. */public class CopyFile {    public static void main(String[] args) {        int i;        FileInputStream fileInputStream = null;        FileOutputStream fileOutputStream = null;        try {            fileInputStream = new FileInputStream("data/CopyFileInput.txt");            fileOutputStream = new FileOutputStream("data/CopyFileOutput.txt");            do {                i = fileInputStream.read();                InputStreamReader inputStreamReader = new InputStreamReader(fileInputStream);                if (i != -1) {                    fileOutputStream.write(i);                }            } while (i != -1);        } catch (IOException e) {            System.out.println("Error input-output: " + e);        } finally {            try {                if (fileInputStream != null) {                    fileInputStream.close();                }            } catch (IOException e2) {                System.out.println("Error close file input");            }            try {                if (fileOutputStream != null) {                    fileOutputStream.close();                }            } catch (IOException e2) {                System.out.println("Error close file output");            }        }    }}